exports.init = function(fs, ip) {
    var date = new Date();
    var hour = date.getHours();
    hour = (hour < 10 ? "0" : "") + hour;
    var min  = date.getMinutes();
    min = (min < 10 ? "0" : "") + min;
    var sec  = date.getSeconds();
    sec = (sec < 10 ? "0" : "") + sec;
    var year = date.getFullYear();
    var month = date.getMonth() + 1;
    month = (month < 10 ? "0" : "") + month;
    var day  = date.getDate();
    day = (day < 10 ? "0" : "") + day;

    var datetime = year + "-" + month + "-" + day + " " + hour + ":" + min + ":" + sec;

    fs.appendFile('/wu/logs/stats.txt', "\n"+ip+" ["+datetime+"]", function(err) {
        if(err) {
            console.log(err);
        } else {
            console.log(ip);
        }
    });
}



